### Top Down Dynamic Programming

182 ms
```
class Solution {
public:

    int dp[100001][2];

    int getMinDeletions(string& s, bool bPrev = false, int current = 0) {
        if(current >= s.size()) return 0;
        if(dp[current][(int)bPrev]) return dp[current][(int)bPrev];
        char ch = s[current];
        if(ch == 'a' && bPrev) { // Delete the current a
            dp[current][(int)bPrev] = 1 + getMinDeletions(s,true,current+1);
            return dp[current][(int)bPrev];
        }
        if(bPrev) { // Continue since current is b
            dp[current][(int)bPrev] =  getMinDeletions(s,true,current+1);
            return dp[current][(int)bPrev];
        }
        if(ch == 'a') { // If no b before and current a
            dp[current][(int)bPrev] = getMinDeletions(s,false,current+1);
            return dp[current][(int)bPrev];
        }
        // Current is b
        dp[current][(int)bPrev] = min(getMinDeletions(s,true,current+1), 1 + getMinDeletions(s,false,current+1));
        return dp[current][(int)bPrev];
    }

    int minimumDeletions(string s) {
        memset(dp,0,sizeof(dp));
        return getMinDeletions(s);
    }
};
```

