class Solution {
    public int minimumDeletions(String s) {
        int aCount = 0;

		// Count total number of a's
        for(int idx = 0; idx < s.length(); idx++) {
            if(s.charAt(idx) == 'a') {
                aCount++;
            }
        }

        int bCount = 0;
        int min = aCount + bCount; // Base case when all numbers needs to be deleted

		// aCount = number of a's on right, bCount = number of b's at left
		// at divide index `i`, there will be no a's at index >= `i` and no b's at index < `i`
		// minimum number to be deleted = number of b's deleted before divide index `i` + number of a's deleted after divide index `i`
        for(int idx = 0; idx < s.length(); idx++) {
            if(s.charAt(idx) == 'a') {
                aCount--; 
            } else {
                bCount++;
            }

            min = Integer.min(aCount + bCount, min);
        }

        return min;

    }
}
