class Solution {
public:
    int minimumDeletions(string s) {
        int ans = 0;

        stack<char> st;

        for(int i=0;i<s.size();i++){

            if(s[i]=='a'){
            if(!st.empty()){
                char top = st.top();
                if(top=='b') ans++;
                st.pop();
                }
            }
            else{
                st.push('b');
            }
        }

        return ans;
    }
};
```

