class Solution {
    public int minimumDeletions(String s) {
        int[] aS = new int[s.length()];
        int[] bS = new int[s.length()];

        int cnt = 0;
        for(int i = s.length() - 1; i >= 0; i--) {
            aS[i] = cnt;
            if(s.charAt(i) == 'a') cnt++;
        }

        cnt = 0;
        for(int i = 0; i< s.length(); i++) {
            bS[i] = cnt;
            if(s.charAt(i) == 'b') cnt++;
        }

        int ans = Integer.MAX_VALUE;
        for(int i = 0; i< s.length(); i++) {
            ans = Math.min(ans , aS[i] + bS[i]);
        }

        return ans;
    }
}
