class Solution {
public:
    int minimumDeletions(string s) {
        int n = s.size(), a = 0, ab = 0;
        for(char &c : s) {
            if (c == 'a') {
                a++;
                ab = max(ab, a);
            } else ab++;
        }
        return n - ab;

