class Solution:
    def minimumDeletions(self, s: str) -> int:        

        left_b = 0  ###Keep track of number of "b" to the left of split point
        right_a = s.count("a") ###Keep track of number of "a" to the right of split point

        min_deletion = right_a ###Delete all a

        ###Iterate through all potential position split point
        for i, char in enumerate(s):
            if char == "a":
                right_a -= 1
            else:
                left_b += 1

            min_deletion = min(min_deletion, left_b + right_a)


        return min_deletion




