class Solution {
public:
    int minimumDeletions(string s) {
        int n=s.size();
        if(n==1)
        {
            if(s[0]=='a')
                return 0;
        }
        vector<int> a(n,0),b(n,0);
        if(s[0]=='b')
        {
            b[0]=1;
        }
        for(int i=1;i<n;i++)
        {
            if(s[i]=='b')
            {
                b[i]=b[i-1]+1;
            }
            else
            {
                b[i]=b[i-1];
            }
        }
        if(s[n-1]=='a')
        {
            a[n-1]=1;
        }
        for(int i=n-2;i>=0;i--)
        {
            if(s[i]=='a')
            {
                a[i]=a[i+1]+1;
            }
            else
            {
                a[i]=a[i+1];
            }
        }
        int mi=n;
        for(int i=1;i<n-1;i++)
        {
            mi=min(mi,a[i+1]+b[i-1]);
        }
        if(b[n-1]==n||a[0]==n)
            return 0;
        mi=min(mi,a[0]);
        mi=min(mi,b[n-1]);
        return mi;
    }
};

