/** https://leetcode.com/problems/minimum-deletions-to-make-string-balanced/
 * @param {string} s
 * @return {number}
 */
var minimumDeletions = function(s) {
  if (s.length === 1) {
    return 0;
  }

  let bCount = Array(s.length+1).fill(0);

  // Count how many 'b' from `0...i-1`
  for (let i = 0; i < s.length; i++) {
    bCount[i+1] = bCount[i];

    if (s[i] === 'b') {
      bCount[i+1] += 1;
    }
  }

  let out = Infinity;

  // Find the least deletion operation needed to make string balance
  // To achieve that, we delete all 'a' on the right + all 'b' on the left of `i`
  for (let i = 0; i < s.length; i++) {
    // How many 'b' on the left
    let bOnLeft = bCount[i];

    // How many 'a' on the right
    // The formula is the length of `s` (0 indexed) - current position `i` minus how many 'b' on the right
    // How many 'b' on the right is easy to calculate by taking last 'b' count and minus how many 'b' at `i+1` then we get 'b' count from `i...s.length-1`
    let length = s.length - 1;
    let bOnRight = bCount[bCount.length - 1] - bCount[i + 1];
    let aOnRight = length - i - bOnRight;

    out = Math.min(out, bOnLeft + aOnRight);
  }

  return out;
};
```

