class Solution {
    public int minimumDeletions(String s) {
        int n = s.length();
        int cntA = 0;
        int cntB = 0;
        int[] leftA = new int[n];
        int[] leftB = new int[n];
        int[] rightA = new int[n];
        int[] rightB = new int[n];
        for (int i = 0; i < n; i++) {
            leftA[i] = cntA;
            leftB[i] = cntB;
            cntA += s.charAt(i) == 'a' ? 1 : 0;
            cntB += s.charAt(i) == 'b' ? 1 : 0;
        }
        cntA = cntB = 0;
        for (int i = n - 1; i >= 0; i--) {
            rightA[i] = cntA;
            rightB[i] = cntB;
            cntA += s.charAt(i) == 'a' ? 1 : 0;
            cntB += s.charAt(i) == 'b' ? 1 : 0;
        }
        int min = n;
        for (int i = 0; i < n; i++) {
            min = Math.min(min, leftB[i] + rightA[i]);
            if (s.charAt(i) == 'a') {
                min = Math.min(min, leftB[i] + rightB[i]);
                min = Math.min(min, leftA[i] + rightA[i] + 1);
            }
            if (s.charAt(i) == 'b') {
                min = Math.min(min, leftA[i] + rightA[i]);
                min = Math.min(min, leftB[i] + rightB[i] + 1);
            }
        }
        return min;
    }
}
