class Solution {
    public int minimumDeletions(String s) {
        int n = s.length();
        int ans=0,a=0;
        for(int i=n-1;i>=0;i--){
            char ch = s.charAt(i);
            if(ch=='a') a++;
            else if(ch=='b' && a!=0){
                ans+=1;
                a--;
            }
        }
        return ans;
    }
}
