class Solution {
public:
    int minimumDeletions(string s) {
        int totalA = 0, totalB = 0;
        for(char c:s) {
            if(c =='a'){
                totalA++;
            } else {
                totalB++;
            }
        }
        int min = std::min(totalA, totalB), pastA=0, pastB=0;
        for(int pos =1; pos<s.size(); pos++) {
            if(s[pos-1]=='a'){
                pastA++;
            } else {
                pastB++;
            }
            if(s[pos-1]=='a'&&s[pos]=='b') {
                int del = totalA-pastA + pastB;
                if(del<min){min = del;}
            }
        }
        return min;
    }
};
```

