class Solution {
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        int limit = 2000 + a + b;
        std::queue<std::pair<int, int>> cur;
        cur.push({0, 1});
        std::unordered_set<int> visited;
        for (auto& x : forbidden){
            visited.insert(x);
        }

        int hops = 0;
        while(!cur.empty()){
            int size = cur.size();
            while(size--){
                auto it = cur.front(); cur.pop();
                int num = it.first;
                int forward = it.second;
                if (num == x) return hops;
                if (visited.count(num) != 0)
                    continue;
                visited.insert(num);
                if (forward){
                    int nxt = num - b;
                    if (nxt >= 0){
                        cur.push({nxt, 0});
                    }
                }
                int nxt = num + a;
                if (nxt <= limit){
                    cur.push({nxt, 1});
                }
            }
            ++hops;
        }
        return -1;

    }
};

