class Solution {
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        std::queue<std::pair<int, bool>> Q; Q.push({0, true});
        std::set<int> visited; 
        for(int place : forbidden) visited.insert(place);

        int count = -1;
        while(!Q.empty()){
            count++;
            int length = Q.size();
            for(int i = 0; i < length; i++){
                auto [curr, forward] = Q.front(); Q.pop();

                if(visited.count(curr) > 0) continue;

                if(curr == x) return count;

                if(forward && curr - b >= 0) Q.push({curr - b, false});
                if(curr - b <= 2000) Q.push({curr + a, true});


                visited.insert(curr);
            }
        }
         return -1;
    }
};
