class Solution {
public:
    int dp[6001][2]; //6000 or 6001 is maximum accessible distance and 2 is state forward/backward
    unordered_map<int,int> mp;//map to retrieve which value is forbidden otherwise we have to tarverse                                   //again and again in forbidden array
    int helper(int i,bool back,int a,int b,int x){
        if(i==x) return 0; 
        if(i<0 || i>6000 || mp.find(i)!=mp.end() || back>=2) return 1e9; //Conddition we will not get ans
        if(dp[i][back]!=-1){
            return dp[i][back]; //if value is already stored return
        }
        dp[i][back]=1+helper(i+a,0,a,b,x); //1 is too denote we have taken 1 steps forward
        if(!back){
            dp[i][back]=min(dp[i][back],helper(i-b,1,a,b,x)+1); //We can not mve consecutively two times                                                                   //back
        }
        return dp[i][back];
    }
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        for(int i=0;i<forbidden.size();i++){
            mp[forbidden[i]]=1;
        }
        memset(dp,-1,sizeof(dp));//Setting alll value of dp to -1
        int ans=helper(0,0,a,b,x);//First 0 will be starting index and second zero means forward step
        return ans>1e9?-1:ans;
    }
};
