class Solution {
public:
    int minimumJumps(vector<int>& f, int a, int b, int x) 
    {
        int res = 0; 
        unordered_set<int> s(f.begin(),f.end());
        queue<pair<int,pair<int,int>>> q; // {coordinate,{jump,prev_state}}
        q.push({0,{0,0}}); // 0 means no backward jump was taken you can go backward
        s.insert(0);// we need not to visit 0 again so we put it in set


        while(q.size() )
        {
            int c=q.front().first;
            //cout<<c<<" ";
            int jump = q.front().second.first;
            int prev = q.front().second.second;
            q.pop();
            if(c==x)
            return jump;
            int back =  c - b ; 
            int forward =  c + a;
            if(prev==0 && back>0 && s.find(back)==s.end())
            {
                q.push({back,{jump+1,1}});// we will change prev to 1 so in next step we can't go backward twice
                s.insert(back); // marks as visited
            }
            if(forward<=10000 && s.find(forward)==s.end())
            {
                q.push({forward,{jump+1,0}});// we will set prev to 0 so we can take backward step in next step 
               s.insert(forward); // marks as visited
            }

        }

        return -1;

   }
};
