class Solution {
    public int minimumJumps(int[] nums, int a, int b, int x) {


        HashSet<Integer> set=new HashSet<>();

        for(int i=0;i<nums.length;i++){
            set.add(nums[i]);
        }

        HashMap<Integer,Integer> dp=new HashMap<>();
        HashSet<Integer> visited=new HashSet<>();
        boolean back=false;
        int res= Sol(set,a,b,0,x,back,dp,visited);

        if(res==Integer.MAX_VALUE) return -1;
        return res;
    }

    public int Sol(HashSet<Integer> set,int a,int b,int pos,int x,boolean back,HashMap<Integer,Integer>               dp,HashSet<Integer> vis){

         int res=Integer.MAX_VALUE;
        if(pos==x){
            return 0;
        }
        if(dp.containsKey(pos)) return dp.get(pos);

        if(pos>6000 || set.contains(pos) || pos<0 || vis.contains(pos) ) return res;

        vis.add(pos);
        int ans=res;
            if(!back)
            ans= Sol(set,a,b,pos-b,x,true,dp,vis);
           // else
            ans= Math.min(ans,Sol(set,a,b,pos+a,x,false,dp,vis));

         if(ans!=Integer.MAX_VALUE){
                res=Math.min(res,1+ans);
            }
        vis.remove(pos);
        dp.put(pos,res);
        return res;

    }
}
