class Solution {
    //1 forward
    //0 backwards
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        set<pair<int,bool>> s; // we need a state of previous position also
        //we are using this set as our dp where int is curresponding to "Position" and bool value represents weather the last jump was in forward or backwards direction!

        for(auto &it :forbidden)
        {
             s.insert({it,false});
             s.insert({it,true});
        }
                            //BFS Traversal
        queue<vector<int>> q;
        q.push({0,1,0});
        s.insert({0,0});
        while(!q.empty())
        {
            int sz = q.size();
            while(sz--)
            {
                int currPos = q.front()[0];
                int prev = q.front()[1];
                int jTill = q.front()[2];
                if(currPos == x) return jTill;
                q.pop();

                //we can jump forward.... irrespective of previous jump
                int fPos = currPos + a;

                if(fPos < 6001 && s.find({fPos,true}) == s.end())
                {
                    q.push({fPos,1,jTill+1});
                }

                //once we encounter a perticular state we store it for future
                s.insert({fPos,true});

                //we can only jump backwards if we have made the previous jump in forward direction
                if(prev == 1)
                {
                    int bPos = currPos - b;
                    if(bPos < 6001 && bPos>=0 && s.find({bPos,false}) == s.end())
                    {
                        q.push({bPos,0,jTill+1});
                    }

                    //once we encounter a perticular state we store it for future
                    s.insert({bPos,false}); 
                }
            }
        }
        return -1;
    }
};
