class Solution {
public:
    set<int> s;
    int dp[6005][3];

    int func(int a,int b,int x,int pos,int prev){
        if (pos==x){
            return 0;
        }

        if (s.count(pos) || pos<0 || pos>6000){
            return 1e9;
        }

        if (dp[pos][prev]!=-1){
            return dp[pos][prev];
        }



        dp[pos][prev]=1+func(a,b,x,pos+a,0);
        if (prev==0){
            dp[pos][prev]=min(dp[pos][prev],1+func(a,b,x,pos-b,1));
        }

     return dp[pos][prev];
    }

    int minimumJumps(vector<int>& v, int a, int b, int x) {
        for (auto it: v){
            s.insert(it);
        }

        memset(dp,-1,sizeof(dp));

        int ans=func(a,b,x,0,0);
        if (ans>=1e9){
            return -1;
        }

     return ans;
    }
};
