class Solution {
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {

        if(x == 0) return 0;

        int dp[6001] = {};

        for(int i: forbidden) dp[i] = -1;

        deque<pair<int, bool>> dq;
        dq.push_back({0, false});

        while(dq.size() > 0){

            auto [val, canGoBack] = dq.front();
            if(val == x) return dp[val];
            dq.pop_front();

            if(canGoBack && val - b > 0 && dp[val-b] == 0){
                dp[val-b] = 1 + dp[val];
                dq.push_back({val-b, false});
            }

            if(val + a <= 6000 && dp[val + a] == 0){
                dp[val + a] = 1 + dp[val];
                dq.push_back({val+a, true});
            }

        }

        return -1;

    }
};
