class Solution {
    class Pair{
        int pos;
        int backcount;
        Pair(int pos,int backcount ){
            this.pos=pos;
            this.backcount=backcount;
        }
    }
    public int minimumJumps(int[] forbidden, int a, int b, int x) {
        HashMap<Integer,Boolean> visited=new HashMap<>();
        Queue<Pair> queue=new LinkedList<>();
        for(int i:forbidden){
            visited.put(i,true);
        }
        queue.add(new Pair(0,0));

        int level=0;
        while(queue.size()>0){
            int size=queue.size();
            while(size-->0){
                Pair rem=queue.remove();
                if(rem.pos==x){
                    return level;
                }
                if(visited.containsKey(rem.pos)){
                    continue;
                }
                visited.put(rem.pos,true);
                if(rem.backcount==0&&rem.pos-b>=0){
                    queue.add(new Pair(rem.pos-b,1));
                }
                if(rem.pos<2000+b){
                    queue.add(new Pair(rem.pos+a,0));
                }
            }level++;
        }
    return -1;

    }
}
