    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {

        //marks forbidden position as visited
        set<int> visited(forbidden.begin(),forbidden.end());

        queue<int> q;
        q.push(0);

        int step = 0;

        while(!q.empty()){
            int sz = q.size();

            //for all current level
            for(int i = 0; i < sz; i++){

                // to eliminate twice backword in a row we are pusing the value with negetive
                //to indicate that past move was backward

                int cur = q.front();
                q.pop();
                bool wasBack = cur < 0;
                cur = abs(cur);

                //if we reach distination
                if(cur == x) return step; 

                int next = cur + a;

                //next < 6000 to handel when we are constantly going forward
                //outside the range of our interest
                // as max of x is 2000
                // and a ,b max is 2000
                if(next < 6000 && visited.find(next) == visited.end()){
                    visited.insert(next);
                    q.push(next);
                }

                next = cur - b;
                //past move wasnot back 
                // we dont need to mark it visted
                //we we might come to it and with forward move and use one more backward
                // to reach destination
                if(!wasBack && next > 0 && visited.find(next) == visited.end()){
                    q.push(next * -1);
                }
            }
             step++;
        }

        return -1;
    }
};
