class Solution {

    public class pair{
        int edge;
        boolean state;
        pair(int edge, boolean state){
            this.edge = edge;
            this.state = state;
        }
    }

    public int minimumJumps(int[] forbidden, int a, int b, int x) {
        ArrayList<Integer> list = new ArrayList<>();
        for(int ele : forbidden){
            // so that search could become easy for forbidden elements.
            list.add(ele);  
        }
        HashSet<String> vis = new HashSet<>(); 
        // taking set as visited so that we don't need to think about size of the visited array 
		// and will add node into it with the jump it came from
		// e.g. if we reached at 2 from backward then we add b2 in visited set.
        Queue<pair> que = new LinkedList<>();
        que.add(new pair(0, false));
        // state true means from backward, false means from forward
        vis.add("f" + 0);  // added from forward
        vis.add("b" + 0);  // added from backward
        int level = 0;
        while(que.size() > 0){
            int size = que.size();
            while(size-- > 0){
                pair rem = que.remove();
                if(rem.edge == x){
                    return level;
                }

                int forward = rem.edge + a;
                int backward = rem.edge - b;
                boolean isback = rem.state;

                if(forward < 6000 && !list.contains(forward) && !vis.contains("f" + forward)){
                    que.add(new pair(forward, false));
                    vis.add("f" + forward);
                }
                if(backward >= 0 && !list.contains(backward) && !isback && !vis.contains("b" + backward)){
                    que.add(new pair(backward, true));
                    vis.add("b" + backward);
                }
            }
            level++;
        }
        return -1;
    }
}
