        if (x == 0)
            return 0;
        Set<Integer> forbiddenSet = new HashSet();
        Set<String> visited = new HashSet();
        for (int n: forbidden)
            forbiddenSet.add(n);
        Queue<Position> queue = new LinkedList();
        queue.offer(new Position(0,0));
        visited.add(0+"#"+0);
        int result = 0;
        int max = 10000;
        while(!queue.isEmpty()){
            int size = queue.size();
            result++;
            while(size-->0){
                Position curr = queue.poll();
                int next_a = curr.val + a;
                if (next_a == x)
