	unordered_map<int,int> mp;

    //here in dp 2D array eg dp[i][j] ,then i saves position & j saves if previously came backward or not
    int dp[6001][2];

    int solve(int i,bool back,int a,int b,int x)
    {
        // if we reach the desired place return 0 ;
        if(i==x) return 0; 

        //if position (i) is in negative or its a forbidden place or its beyond x
        //then return a max value;
        if(i<0 || mp.find(i)!=mp.end() || i>6000)
            return 1e9;

        //memoziation
        if(dp[i][back]!=-1) return dp[i][back];


        // go forward that is i+a;

        dp[i][back]=1+solve(i+a,false,a,b,x);

        //if back is false then previouly we didn't go backward now we can
        if(!back)
        {
            // now we go backward make backward off 

            dp[i][back]=min(dp[i][back],1+solve(i-b,1,a,b,x));

            //here we are taking minimum from going forward & backward;
        }
        return dp[i][back];
    }

    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {

        //save the forbidden cordinates to fetch in O(1) operation;
        for(auto &x :forbidden) mp[x]++; 

        memset(dp,-1,sizeof(dp));

        // here second argument is false beccause at first we can go backward;
        int ans=solve(0,false,a,b,x);

        if(ans>=1e9) return -1; //

        return ans;
    }
};

