class Solution {
public:
    bool s[20000], v[20000];
    int minimumJumps(vector<int>& fb, int a, int b, int x) {
        queue<pair<int, int>> q;
        q.emplace(0, 0);
        int m = max(a, b);
        for (auto f : fb) s[f] = true;
        for (int i = 0; !q.empty(); i++) {
            for (int j = 0, e = q.size(); j < e; j++) {
                auto [cur, back] = q.front(); q.pop();
                if (cur == x) return i;

                if (cur <= x + 5 * m && !s[cur + a]) {
                    s[cur + a] = true;;
                    q.emplace(cur + a, 0);
                }
                if (cur - b > 0 && !back && !s[cur - b] && !v[cur - b]) {
                    v[cur - b] = true;
                    q.emplace(cur - b, 1);
                }
            }
        }
        return -1;
    }
};
