class Solution {
public:
    int dp[2][6005];
    int fun(int idx,int a,int b,int x,int back)
    {
         if(idx==x)
            return 0;
        if(idx>=6005||idx<0||dp[back][idx]==-2)
        {
            return 1e9;
        }

        if(dp[back][idx]!=-1)
            return dp[back][idx];


        dp[back][idx]=1+fun(idx+a,a,b,x,0);
        if(!back&&a!=b)
        {
            dp[back][idx]=min(dp[back][idx],1+fun(idx-b,a,b,x,1));
        }

        return dp[back][idx];
    }               
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        if(x==0)
            return 0;
        int i,n=forbidden.size();
        for(i=0;i<6005;i++)
        {
            dp[0][i]=-1;
            dp[1][i]=-1;
        }

        for(i=0;i<n;i++)
        {
            dp[0][forbidden[i]]=-2;
            dp[1][forbidden[i]]=-2;
        }

        int res=fun(0,a,b,x,0);
        if(res>=1e9)
            return -1;
        return res;
    }
};
