class Solution(object):
    def is_pos_valid(self, pos, forbidden_map):
        if pos in forbidden_map or pos < 0:
            return False
        return True

    def calculate_jumps(self, dp, pos, b_jump, fwd, bwd):
        if fwd < 0 and bwd < 0:
            dp[str(pos) + "$" + str(b_jump)] = -1
            return -1
        elif fwd >= 0 and bwd < 0:
            dp[str(pos) + "$" + str(b_jump)] = fwd + 1
            return fwd + 1
        elif bwd >= 0 and fwd < 0:
            dp[str(pos) + "$" + str(b_jump)] = bwd + 1
            return bwd + 1

        dp[str(pos) + "$" + str(b_jump)] = min(fwd, bwd) + 1

        return min(fwd, bwd) + 1

    def get_min_jumps(self, a, b, x, forbidden_map, current_pos, jumps, dp, visited, b_jumps):
        if str(current_pos) + "$" + str(b_jumps) in dp:            
            return dp[str(current_pos) + "$" + str(b_jumps)]

        if current_pos in visited and visited[current_pos]:
            return -1

        if not self.is_pos_valid(current_pos, forbidden_map):
            return -1

        if current_pos > 2000+a+b:
            return -1

        if b_jumps >= 2:
            return -1

        if current_pos == x:
            return 0

        visited[current_pos] = True
        fwd = self.get_min_jumps(a, b, x, forbidden_map, current_pos+a, jumps, dp, visited, 0)
        bwd = self.get_min_jumps(a, b, x, forbidden_map, current_pos-b, jumps, dp, visited, b_jumps+1)
        visited[current_pos] = False

        return self.calculate_jumps(dp, current_pos, b_jumps, fwd, bwd)

    def create_forbidden_map(self, forbidden):
        forbidden_map = {}
        for pos in forbidden:
            forbidden_map[pos] = True

        return forbidden_map

    def minimumJumps(self, forbidden, a, b, x):
        max_num = pow(10, 7)
        forbidden_map = self.create_forbidden_map(forbidden)
        dp = {}
        return self.get_min_jumps(a,b,x,forbidden_map,0, 0, dp, {}, 0)
