class Solution {
    public int minimumJumps(int[] forbidden, int a, int b, int x) {
        Queue<Pair<Integer, Integer>> queue = new LinkedList<>();
        Set<Pair<Integer, Integer>> set = new HashSet<>();
        int max = a + b + x;
        for (int i = 0; i < forbidden.length; i++) {
            set.add(new Pair<Integer, Integer>(forbidden[i], 0));
            set.add(new Pair<Integer, Integer>(forbidden[i], 1));
            max = Math.max(max, forbidden[i] + a + b);
        }
        queue.offer(new Pair<Integer, Integer>(0, 0));
        set.add(queue.peek());
        int steps = 0;
        while (!queue.isEmpty()) {
            int size = queue.size();
            while (size-- > 0) {
                Pair<Integer, Integer> p = queue.poll();
                if (p.getKey() == x) return steps;
                Pair<Integer, Integer> next = new Pair<Integer, Integer>(p.getKey() + a, 0);
                Pair<Integer, Integer> prev = new Pair<Integer, Integer>(p.getKey() - b, p.getValue() + 1);
                if (next.getKey() <= max && !set.contains(next)) {
                    set.add(next);
                    queue.offer(next);
                }
                if (prev.getKey() >= 0 && !set.contains(prev) && prev.getValue() < 2) {
                    set.add(prev);
                    queue.offer(prev);
                }
            }
            steps++;
        }
        return -1;
    }
}
