			return 0;
		for (int f : forbidden)
			fset.add(f);
		if (fset.contains(a) || fset.contains(x))
			return -1;

		int maxLen=10000;

		axis=new int[maxLen];
		Arrays.fill(axis, -1);
		axis[0]=0;
		axis[a]=1;
		int cnt=1;
		Queue<Integer> backs = new LinkedList<>();
		for (int i=a;i<maxLen;i+=a) {
			if (fset.contains(i))
				break;
			if (i==x)
				return cnt;
			axis[i]=cnt;
			cnt++;
			if (i-b>0 && axis[i-b]<0 && !fset.contains(i-b))
				backs.add(i-b);
		}
		if (b==a)
			return -1;

		while (!backs.isEmpty()) {
			int size=backs.size();
			for (int i=0;i<size;i++) {
				int back=backs.remove();
				int base=axis[back+b]+1;
				axis[back]=base;
				if (back==x)
					return base;
				base++;
				for (int j=back+a;j<maxLen;j+=a) {
					if (fset.contains(j))
						break;
					if (axis[j]>0)
						break;
					axis[j]=base;
					if (j==x)
						return base;
					base++;
					if (j-b>0 && axis[j-b]<0 && !fset.contains(j-b))
						backs.add(j-b);
				}
			}
		}
		return -1;
    }

