var minimumJumps = function(forbidden, a, b, x) {
	forbidden = new Set(forbidden);
	if (forbidden.has(0) || forbidden.has(x)) return -1;
	if (x === 0) return 0;
	const seta = new Set([0]);
	const setb = new Set([0]);

	const queue = [[0,0]];
	let step = 0;

	while (queue.length) {
		let size = queue.length;

		for (let i = 0; i < size; i++) {
			let [curr, time] = queue.shift();
			let na = curr + a;

			if (na >= 0 && !seta.has(na) && !forbidden.has(na)) {
				if (na === x) return step+1;
				if (!(a >= b && na > x+2*b)) {
					queue.push([na, 0]);
					seta.add(na);   
				}
