class Solution {
    public int minimumJumps(int[] forbidden, int a, int b, int x) {
         int steps = 0, furthest = x + a + b;
        Queue<Pair<Integer, Integer>> q = new LinkedList();
        q.offer(new Pair(0, 0));
        Set<Pair<Integer, Integer>> set= new HashSet<>(q);
        for (int cur : forbidden) {
            set.add(new Pair(0, cur));
            set.add(new Pair(1, cur));
            furthest = Math.max(furthest, cur + a + b);
        }
        while (!q.isEmpty()) {
            for (int sz = q.size(); sz > 0; --sz) {
                Pair<Integer, Integer> p = q.poll();
                int dir = p.getKey(), cur = p.getValue();
                if (cur == x) {
                    return steps;
                }
                Pair<Integer, Integer> forward = new Pair<>(0, cur+ a), backward = new Pair<>(1, cur- b);
                if (cur + a <= furthest && set.add(forward)) {
                    q.offer(forward);
                }
                if (dir == 0 && cur - b >= 0 && set.add(backward)) {
                    q.offer(backward);
                }
            }
            ++steps;
        }
        return -1;                
    }
}
