class Solution {
    public int minimumJumps(int[] forbidden, int a, int b, int x) {
        if (x == 0) return 0;

        int max=0;
        for(int f:forbidden)max=Math.max(max,f);
        max=Math.max(max,x);
        boolean[][] visited = new boolean[max+a+b+1][2];

        for (int f : forbidden) {
            visited[f][0] = true;
            visited[f][1] = true;
        }

        Queue<Pair> q = new LinkedList<>();
        q.offer(new Pair(0, 0, true));
        visited[0][0] = true;

        while (!q.isEmpty()) {
            Pair p = q.poll();
            if (p.idx == x) return p.jumps;

            int f = p.idx + a;
            if (f <= max+a+b && !visited[f][0]) {
                Pair forw = new Pair(f, p.jumps + 1, false);
                visited[f][0] = true;
                q.offer(forw);
            }

            if (p.flag) continue;

            int back = p.idx - b;
            if (back > 0 && !visited[back][1]) {
                Pair backward = new Pair(back, p.jumps + 1, true);
                visited[back][1] = true;
                q.offer(backward);
            }
        }

        return -1;
    }
}

class Pair {
    int idx;
    int jumps;
    boolean flag;

    Pair(int idx, int jumps, boolean flag) {
        this.idx = idx;
        this.jumps = jumps;
        this.flag = flag;
    }
}


