```
class Solution {
public:
    int temp[1001];
    bool dp[51][1025];
    int cache[1025];
    int n;
    vector<int>a;
    bool solve(int ind, int mask){
        if(mask == 0)return true;
        if(ind == n)return false;
        if(!dp[ind][mask])return dp[ind][mask];
        for(int i = mask; i > 0; i = mask & (i - 1)){
            if(cache[i] <= a[ind] && solve(ind + 1, mask ^ i))return dp[ind][mask] = true;
        }
         return dp[ind][mask] = solve(ind + 1, mask);
    }
    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        for(int i : nums)temp[i]++;

        for(int i = 0; i < 1001; ++i){
            if(temp[i])a.push_back(temp[i]);
        }
        n = a.size();
        int m = quantity.size();
        for(int i = 0; i < 51; ++i)for(int j = 0; j < 1025; ++j)dp[i][j] = true;
        for(int i = 0; i < (1 << m); ++i){
            for(int b = 0; b < m; ++b){
                if((i >> b) & 1)cache[i] += quantity[b];
            }
        }
        return solve(0, (1 << m) - 1);

    }
};
