class Solution {
public:
    unordered_map<int,int> m ;
    bool ans = false ;
    vector<int> occurrences ;

    void f(vector<int>& qt,int i=0,int j=0){
        if (i==qt.size())  ans = true  ;
        if (ans) return  ;
        for (int k=j;k<occurrences.size();k++){
            if (qt[i]<=occurrences[k]){
                occurrences[k] = occurrences[k] - qt[i] ;
                f(qt,i+1,j) ;
                occurrences[k] = occurrences[k] + qt[i] ;
            }
        }
    }

