      {
	int j = 0, k = 0, l = 0;

	for (j = 1; j < r.length; j++)  //case 1 loop
	  {

	    if (quantity[i] == r[j])
	      {
		r[j] = 0;

		break;
	      }
	  }
	if (j == r.length)
	  {
	    for (k = 1; k < r.length; k++)     //case 2 loop
	      {
		if (quantity[i] < r[k])
		  {
		    if (r[k] != 0 && (r[k] % quantity[i]) == 0)
		      {

			r[k] = (r[k] - quantity[i]);

			break;
