    def canDistribute(self, nums: List[int], quantity: List[int]) -> bool:
        # BFS bismask
        # Time: O(3^m) 
		# ref : https://cp-algorithms.com/algebra/all-submasks.html

        counts = sorted(
            Counter(nums).values(),
            reverse = True
        )[:len(quantity)]
        N, M = len(counts), len(quantity)

        subsums = [0] * (1 << M)
        for mask in range(1 << M):
            for i in range(M):
                if mask & (1 << i):
                    subsums[mask] += quantity[i]

        # q is reachable masks (of satisfied customers) in level i (counts[:i] have been exhausted)
        q = {0}
        for i in range(N):
            q_next = set()
            for mask in q:
                not_mask = ((1 << M) - 1) ^ mask
                submask = not_mask
                while submask:
                    if subsums[submask] <= counts[i]:
                        next_mask = mask | submask
                        if next_mask == (1 << M) - 1:
                            return True
                        q_next.add(next_mask)
                    submask = (submask - 1) & not_mask
            q = q_next
