class Solution {
public:
    bool dfs(vector<int>& freq, vector<int>& q, int index) {
        if (index >= q.size()) return true;

        int j = 0;
        for (int i = 0; i < freq.size(); i = j) {
            // Optimization 2: reduce double count.
            for (j = i; j < freq.size() && freq[j] == freq[i]; j++);

            if (q[index] > freq[i]) continue;
            freq[i] -= q[index];
            if (dfs(freq, q, index + 1)) {
                return true;
            }
            freq[i] += q[index];
        } 

        return false;
    }

    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        int total = 0;
        for (auto q: quantity) total += q;
        if (total > nums.size()) return false;

        unordered_map<int, int> m1;
        for (auto n: nums) m1[n]++;
        vector<int> freq;
        for (auto it: m1) freq.push_back(it.second);

        // Optimization 1: Greedy, start from the large integer.
        sort(quantity.begin(), quantity.end(), greater<int>());
        sort(freq.begin(), freq.end(), greater<int>());
        return dfs(freq, quantity, 0);
    }
};
