/*
Time Complexity = O(N * (3 ^ M)) where N = 'unique values' in nums ( = 50), M = size of 'quantity'
*/

class Solution {
    public boolean canDistribute(int[] nums, int[] quantity) {
        int rows = 0, cols = quantity.length;
        Map<Integer, Integer> frequencyByNumber = new HashMap<Integer, Integer>();
        for(int i = 0; i < nums.length; i++) {
            frequencyByNumber.put(nums[i], 1 + frequencyByNumber.getOrDefault(nums[i], 0));
        }
        List<Integer> numbers = new ArrayList<Integer>(frequencyByNumber.keySet());
        rows = numbers.size();
        int[] requirement = new int[(1 << cols)];
        for(int mask = 0; mask < (1 << cols); mask++) {
            for(int i = 0; i < cols; i++) {
                if((mask & (1 << i)) != 0) {
                    requirement[mask] += quantity[i];
                }
            }
        }
        boolean[][] DP = new boolean[rows + 1][(1 << cols)];
        DP[0][0] = true;
        for(int i = 0; i < rows; i++) {
            for(int mask = ((1 << cols) - 1); mask >= 0; mask--) {
                DP[i + 1][mask] = DP[i + 1][mask] || DP[i][mask];
                for(int subMask = mask; subMask > 0; subMask = ((subMask - 1) & mask)) {
                    if(requirement[subMask] <= frequencyByNumber.get(numbers.get(i)) && DP[i][mask ^ subMask]) {
                        DP[i + 1][mask] = true;
                    }
                }
            }
        }
        return DP[rows][(1 << cols) - 1];
    }
}
