from collections import Counter
import bisect
class Solution:
    def canDistribute(self, nums: List[int], quantity: List[int]) -> bool:
        nums = list(Counter(nums).values())
        nums.sort()

        quantity.sort(reverse=True)

        def solve(qi):
            if qi == len(quantity):
                return True
            elif len(nums)==0:
                return False
            else:
                cs = bisect.bisect_left(nums, quantity[qi])
