class Solution {
private:
    bool helper(vector<int> &frq, vector<int> &quantity, int idx){
        // Base condition
        if(idx >= quantity.size()) return true;

        for(auto &f : frq){
            if(f >= quantity[idx]){
                f -= quantity[idx];
                if(helper(frq, quantity, idx + 1)) return true;
                // Back tracking
                f += quantity[idx];
            }
        }

        return false;

    }

public:
    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        // Intution
        // The idea in here is really very simple. First we will count the frequencies of characters present in the nums vector and then we will store them inside a map
        unordered_map<int, int> mp;
        for(auto & num : nums){
            mp[num] += 1;
        }
        vector<int> frq;
        for(auto itr = mp.begin() ; itr != mp.end() ; itr++){
            frq.push_back(itr->second);
        }
        // Reverse sorting the frq and the quantity array so that the frequencies can be correctly related
        sort(frq.rbegin(), frq.rend());
        sort(quantity.rbegin(), quantity.rend());
        return helper(frq, quantity, 0);
    }
};
