#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        unordered_map<int, int> freqMap;
        for (int num : nums) {
            freqMap[num]++;
        }
        sort(quantity.rbegin(), quantity.rend());
        vector<int> uniqueNums;
        for (auto& entry : freqMap) {
            uniqueNums.push_back(entry.first);
        }

        return backtrack(uniqueNums, freqMap, quantity, 0);
    }

    bool backtrack(vector<int>& uniqueNums, unordered_map<int, int>& freqMap, vector<int>& quantity, int idx) {
        if (idx == quantity.size()) {
            return true; // All customers are satisfied
        }

        for (int i = 0; i < uniqueNums.size(); i++) {
            int num = uniqueNums[i];
            if (freqMap[num] >= quantity[idx]) {
                freqMap[num] -= quantity[idx]; // Decrease the frequency of current number
                if (backtrack(uniqueNums, freqMap, quantity, idx + 1)) {
                    return true;
                }
                freqMap[num] += quantity[idx]; // Backtrack by increasing the frequency
            }
        }

        return false;
    }
};

