class Solution {
public:
    bool solve(map<int, int> &mp, vector<int>& qt, int i){
        if(i == qt.size())
        return true;

        for(auto it:mp){
            if(it.second >= qt[i]){
                mp[it.first] = mp[it.first] - qt[i];

                bool ans = solve(mp, qt, i+1);

                if(ans == true)
                return true;
                else
                mp[it.first] = mp[it.first] + qt[i];
            }
        }
        return false;
    }
