def canDistribute(self, nums: List[int], q: List[int]) -> bool:
	nums = sorted(nlargest(len(q), Counter(nums).values()), reverse=True)
	q = sorted(q, reverse=True)

	@lru_cache(None)
	def dfs(tup, i):
		if i == len(q):
			return True
		for k in range(len(tup)):
			if tup[k] >= q[i]:
				nxt = list(tup[:k])+[tup[k]-q[i]]+list(tup[k+1:])
				if dfs(tuple(sorted(nxt, reverse=True)), i+1):
					return True
		return False

	return dfs(tuple(nums), 0)
