class Solution {
public:
    #define ll long long
    ll dp[1<<10][51];
    vector<ll>pre;

    ll solve(vector<ll>&container,vector<int>&quantity,ll mask,ll index,ll m,ll n){
        if(mask==(1<<m)-1){
            return 1;
        }
        if(index>=n){
            return 0;
        }
        if(dp[mask][index]!=-1){
            return dp[mask][index];
        }
        ll answer=0,current=mask^((1<<m)-1);
        answer=(answer+solve(container,quantity,mask,index+1,m,n));
        for(ll i=current;i>0;i=(i-1)&current){
            if(pre[i]>container[index]){
                continue;
            }
            answer=(answer+solve(container,quantity,mask|i,index+1,m,n));
        }
        return dp[mask][index]=answer;
    }

    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        ll m=quantity.size();
        memset(dp,-1,sizeof(dp));
        unordered_map<ll,ll>mp1;
        for(auto &x:nums){
            mp1[x]++;
        }
        vector<ll>container;
        for(auto &x:mp1){
            container.push_back(x.second);
        }
        pre.resize(1<<m);
        for(ll i=0;i<(1<<m);i++){
            ll sum=0;
            for(ll j=0;j<m;j++){
                if(i&(1<<j)){
                    sum+=quantity[j];
                }
            }
            pre[i]=sum;
        }
        ll n=container.size();
        return solve(container,quantity,0,0,m,n);
    }
};
