/*
Intution :

1-> Count the frequencies and make an array out of it
2-> Sort the quantities in descending order. We do this to get an early termination if for
    the max value in quantity we are not able to find a collection of integers
3-> Call the recursive function
4-> Recursive Code is explained below

*/

class Solution {
public:

    bool solve(vector<int>& freq, vector<int>& quantity, int idx) {

        // If we have fullly traversed the quantity array means that we were able to satisfy
        // everyone so return true
        if(idx == quantity.size())
            return true;

        // For every 'quantity' value check if freq array has a value that can satisfy it
        // If yes call the recursive function for the next quantity value 

        // As true will only be hit when we get to the end of the 'quantity' array 
        // This would lead to a chain of return true statements that would finally return
        // true to our calling function

        for(int i=0; i<freq.size(); i++) {

            if(freq[i] >= quantity[idx]) {
                freq[i] -= quantity[idx];

                // Calling Recursively
                if(solve(freq,quantity,idx+1)) {
                    return true;
                }
                // backtracting step
                // as it may be possible that we would want to assign a different collection 
                // of integers to this particular customer
                else {
                    freq[i] += quantity[idx];
                }
            }
        }
        return false;
    }

    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        sort(nums.begin(), nums.end());
        vector<int> freq;

        // 1)
        for(int i=0; i<nums.size(); i++) {
            if(i == nums.size()-1) {
                freq.push_back(1);
                continue;
            }

            int count = 1;
            int n = nums[i];
            if(i+1 < nums.size())
                i += 1;

            while(true and i<nums.size()) {
                if(nums[i] == n) {
                    count++;
                    i++;
                }
                else {
                    break;
                }
            }
            i -= 1;
            freq.push_back(count);
        }
        // ---------------------------------------------------------------------------------

        // 2)
        auto compare = [](auto &a, auto &b) {
            return a > b;
        };
        sort(quantity.begin(), quantity.end(), compare); 

        // ---------------------------------------------------------------------------------

        // 3)
        return solve(freq, quantity, 0);
    }
};
```

