class Solution:
    def canDistribute(self, nums: List[int], qy: List[int]) -> bool:
        fr=Counter(nums)
        frx=[fr[k] for k in fr]
        rbm=[0]
        for i in range(len(qy)):rbm[0]|=1<<i
        @cache
        def dp(fi,ost,bm):
            if bm==rbm[0]:return True
            if fi>=len(frx):return True if bm==rbm[0] else False
            if ost==0:
                if fi+1>=len(frx):return True if bm==rbm[0] else False
                return dp(fi+1,frx[fi+1],bm)
            for i in range(len(qy)):
                k=1<<i
                if bm&k!=0:continue
                if qy[i]<=ost:
                    if dp(fi,ost-qy[i],bm|k):return True
            if fi+1>=len(frx):return False  
            return dp(fi+1,frx[fi+1],bm)
        return dp(0,frx[0],0)
