    vector<unordered_set<int>> dp;
    int customerSatisfied(int customers, int customer){
        return customers & (1 << customer);
    }

    int newCustomer(int customers, int customer){
        return customers ^ (1 << customer);
    }

    int find(int i, vector<int> &nums, vector<int>& quantity, int* cond, int customers, int n, int q){
        if(customers == (1 << q) - 1) return true;
        if(i == n) return false;
        //cout << i << endl;
        if(dp[i].find(customers) != dp[i].end()) return false;

        bool res;
        for(int j = 0; j < q; j++){
            if(customerSatisfied(customers, j)) continue;
            if(cond[i] - i + 1 >= quantity[j]){
                res = find(i + quantity[j], nums, quantity, cond, newCustomer(customers, j), n, q);
                if(res) return true;
            }
        }

        res = find(cond[i]+1, nums, quantity, cond, customers, n, q);
        if(res) return true;
        dp[i].insert(customers);
        return false;

    }

public:
