class Solution:
    def canDistribute(self, nums: List[int], quantity: List[int]) -> bool:
        #Backtracking approach
        counter = {}
        for num in nums:
            counter[num] = counter.get(num, 0) + 1
        freq_counter = {val: sum(1 for key in counter.keys() if counter[key] == val) for val in counter.values()}
        res = False
        #Call inner function with quantity array sorted in reverse
        def check_distribute(freq_counter, quantity):
            #print(f'Currently: freq_counter {freq_counter} and quantity: {quantity}')
            nonlocal res
            if not quantity:
                #All customers served
                #print('!')
                res = True
                return 
            else:
                if not res:
                    quant = quantity[-1]
                    for key in freq_counter:
                        if key < quant or freq_counter[key] == 0:
                            continue
                        else:
                            diff = key - quant
                            new_count = freq_counter[key] - 1
                            check_distribute({**freq_counter, key: new_count, diff: freq_counter.get(diff,0) + 1},quantity[:-1])
        #Call inner function 
        check_distribute(freq_counter,sorted(quantity))
        return res 
