        Dictionary<int, int> count = new Dictionary<int, int>();

        for(int i = 0; i < nums.Length; i++)
        {
            if(!count.ContainsKey(nums[i]))
                count.Add(nums[i], 0);
            count[nums[i]]++;
        }

        return CanDistribute(count, count.Keys.ToList<int>(), quantity, 0);
    }

    Dictionary<string, bool> memo = new Dictionary<string, bool>();
    private bool CanDistribute(Dictionary<int, int> count, List<int> keys, int[] quantity, int start)
    {
        if(start >= quantity.Length) return true;

        List<int> vals = count.Values.ToList();
        vals.Sort();
        string key = start+"#"+string.Join(" ", vals);

        if(memo.ContainsKey(key)) return memo[key];
        foreach(var k in keys)
        {
            if(count[k] >= quantity[start])
            {
                count[k] -= quantity[start];
                if(CanDistribute(count, keys, quantity, start+1))
                    return memo[key] = true;
                count[k] += quantity[start];
            }
        }

        return memo[key] = false;
    }
}

