        m = len(quantity)

        unique_nums = sorted(c.values())[-m:]

        quantity.sort(reverse=True)

        def backtrack(unique_nums, cust):
            if cust == len(quantity): return True

            for i, v in enumerate(unique_nums):
                if v < quantity[cust]: continue

                unique_nums[i] -= quantity[cust]
                if backtrack(unique_nums, cust+1): return True
                unique_nums[i] += quantity[cust]

        return backtrack(unique_nums, 0)
